/*jshint esversion: 6 */
// define correct version of ecmascript for jshint

const highScoresList = document.getElementById('highScoresList');
const scores = JSON.parse(localStorage.getItem('scores')) || [];

// Set the inner HTML of the highScoresList element to a string 
// generated by mapping over the scores array and generating an HTML list item for each score
// then joining the items into a single string.
let template = '';

scores.forEach(score => {  
    template += `<li class="high-score">${score.name} - ${score.scoreText}</li>`;  
});
highScoresList.innerHTML = template;