var highScoresList = document.getElementById('highScoresList')
var scores = JSON.parse(localStorage.getItem('scores')) || []

// Set the inner HTML of the highScoresList element to a string 
// generated by mapping over the scores array and generating an HTML list item for each score
// then joining the items into a single string.
highScoresList.innerHTML = // from the video
scores.map(score => {   // from the video
    return `<li class="high-score">${score.name} - ${score.scoreText}</li>`   // from the video
}).join('') // from the video